import 'guards/auth_guard.dart';

final appRouterProvider = Provider<GoRouter>((ref) {
  return GoRouter(
    initialLocation: AppRoutes.splash,
    debugLogDiagnostics: true,
    redirect: (context, state) => brandGuard(ref, state),
    routes: [
      GoRoute(
        path: AppRoutes.brandSelector,
        builder: (_, __) => const BrandSelectorPage(),
      ),

      /// BOTTOM NAV + MAIN SHELL
      ShellRoute(
        builder: (_, __, child) => MainNavigation(child: child),
        routes: [
          GoRoute(
            path: AppRoutes.home,
            builder: (_, __) => const HomePage(),
          ),
          GoRoute(
            path: AppRoutes.order,
            builder: (_, __) => const OrderPage(),
          ),
          GoRoute(
            path: AppRoutes.waiting,
            builder: (_, __) => const WaitingListPage(),
          ),
          GoRoute(
            path: AppRoutes.inbox,
            builder: (_, __) => const InboxPage(),
          ),
          GoRoute(
            path: AppRoutes.account,
            builder: (_, __) => const AccountPage(),
          ),
        ],
      ),
    ],
  );
});
```

