import '../../../brand/brand_provider.dart';
import '../routes.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:go_router/go_router.dart';

String? brandGuard(WidgetRef ref, GoRouterState state) {
  final brand = ref.read(selectedBrandProvider);

  // Always allow Splash & Landing
  if (state.location == AppRoutes.splash ||
      state.location == AppRoutes.landing) {
    return null;
  }

  // If no brand selected → landing
  if (brand == null) {
    return AppRoutes.landing;
  }

  return null;
}
