```dart
class MainNavigation extends StatelessWidget {
  final Widget child;

  const MainNavigation({super.key, required this.child});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: child,
      bottomNavigationBar: Container(
        height: 72.h,
        padding: EdgeInsets.only(bottom: 8.h),
        child: BottomNavigationBar(
          selectedFontSize: 12.sp,
          unselectedFontSize: 11.sp,
          currentIndex: _index(context),
          onTap: (i) => _navigate(context, i),
          items: [
            BottomNavigationBarItem(
              icon: Icon(Icons.home, size: 22.sp),
              label: "Home",
            ),
            BottomNavigationBarItem(
              icon: Icon(Icons.receipt, size: 22.sp),
              label: "Order",
            ),
            BottomNavigationBarItem(
              icon: Icon(Icons.timer, size: 22.sp),
              label: "Waiting",
            ),
            BottomNavigationBarItem(
              icon: Icon(Icons.inbox, size: 22.sp),
              label: "Inbox",
            ),
            BottomNavigationBarItem(
              icon: Icon(Icons.person, size: 22.sp),
              label: "Account",
            ),
          ],
        ),
      ),
    );
  }

  int _index(BuildContext context) {
    final location = GoRouterState.of(context).uri.toString();
    if (location.startsWith(AppRoutes.order)) return 1;
    if (location.startsWith(AppRoutes.waiting)) return 2;
    if (location.startsWith(AppRoutes.inbox)) return 3;
    if (location.startsWith(AppRoutes.account)) return 4;
    return 0;
  }

  void _navigate(BuildContext context, int index) {
    switch (index) {
      case 0:
        context.go(AppRoutes.home);
        break;
      case 1:
        context.go(AppRoutes.order);
        break;
      case 2:
        context.go(AppRoutes.waiting);
        break;
      case 3:
        context.go(AppRoutes.inbox);
        break;
      case 4:
        context.go(AppRoutes.account);
        break;
    }
  }
}
```

