int routeToIndex(String location) {
  if (location.startsWith(NameRouter.home)) return 0;
  if (location.startsWith(NameRouter.brand)) return 1;
  if (location.startsWith(NameRouter.waiting)) return 2;
  if (location.startsWith(NameRouter.order)) return 3;
  if (location.startsWith(NameRouter.reward)) return 4;
  return 0;
}
