```dart
// home_page_event.dart
abstract class HomePageEvent {}

class HomePageStarted extends HomePageEvent {}
class HomePageRefreshAll extends HomePageEvent {}
```

```dart
// home_page_state.dart
abstract class HomePageState {}

class HomePageReady extends HomePageState {}
class HomePageRefreshing extends HomePageState {}
```

```dart
// home_page_bloc.dart
class HomePageBloc extends Bloc<HomePageEvent, HomePageState> {
  HomePageBloc() : super(HomePageReady()) {
    on<HomePageStarted>((_, emit) => emit(HomePageReady()));
    on<HomePageRefreshAll>((_, emit) async {
      emit(HomePageRefreshing());
      // hanya trigger, bukan fetch data
      await Future.delayed(const Duration(milliseconds: 300));
      emit(HomePageReady());
    });
  }
}
```

