  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final location = GoRouterState.of(context).uri.toString();

    int index = 0;
    if (location.startsWith(Routes.cart)) index = 1;
    if (location.startsWith(Routes.profile)) index = 2;

    return BottomNavigationBar(
      currentIndex: index,
      onTap: (value) {
