// main.dart
import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:http/http.dart' as http;

/// ==== MODEL BRAND ====

enum Brand { a, b, c, d, e }

extension BrandX on Brand {
  String get label {
    switch (this) {
      case Brand.a:
        return 'Brand A';
      case Brand.b:
        return 'Brand B';
      case Brand.c:
        return 'Brand C';
      case Brand.d:
        return 'Brand D';
      case Brand.e:
        return 'Brand E';
    }
  }

  /// Contoh endpoint per brand (GANTI dengan URL API kamu)
  String get apiEndpoint {
    // misal: https://api.yourdomain.com/dashboard/a, /b, dst
    return 'https://your-api.com/dashboard/${name}';
  }
}

/// ==== PROVIDERS ====

/// Brand yang sedang dipilih dari drawer
final selectedBrandProvider = StateProvider<Brand>((ref) => Brand.a);

/// Index bottom navigation
final bottomTabIndexProvider = StateProvider<int>((ref) => 0);

/// Initial hit API per brand untuk halaman Home
final homeDataProvider = FutureProvider.family<String, Brand>((ref, brand) async {
  final uri = Uri.parse(brand.apiEndpoint);

  final response = await http.get(uri);

  if (response.statusCode != 200) {
    throw Exception('Gagal load data dashboard untuk ${brand.label}');
  }

  // Contoh: API mengembalikan JSON: { "message": "..." }
  final decoded = json.decode(response.body) as Map<String, dynamic>;
  final message = decoded['message']?.toString() ?? 'Berhasil ambil data ${brand.label}';

  return message;
});

/// ==== MAIN APP ====

void main() {
  runApp(const ProviderScope(child: MyApp()));
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Multi Brand Dashboard',
      debugShowCheckedModeBanner: false,
      theme: ThemeData(
        useMaterial3: true,
        colorSchemeSeed: Colors.blue,
      ),
      home: const DashboardShell(),
    );
  }
}

/// ==== DASHBOARD SHELL (DRAWER + BOTTOM NAV) ====

class DashboardShell extends ConsumerWidget {
  const DashboardShell({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final selectedBrand = ref.watch(selectedBrandProvider);
    final bottomIndex = ref.watch(bottomTabIndexProvider);

    return Scaffold(
      appBar: AppBar(
        title: Text('Dashboard - ${selectedBrand.label}'),
      ),
      drawer: Drawer(
        child: ListView(
          children: [
            const DrawerHeader(
              child: Text(
                'Pilih Brand',
                style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold),
              ),
            ),
            for (final brand in Brand.values)
              ListTile(
                title: Text(brand.label),
                selected: brand == selectedBrand,
                onTap: () {
                  // Ganti brand
                  ref.read(selectedBrandProvider.notifier).state = brand;

                  // Optional: selalu balik ke Home tiap ganti brand
                  ref.read(bottomTabIndexProvider.notifier).state = 0;

                  Navigator.of(context).pop();
                },
              ),
          ],
        ),
      ),
      body: _buildBody(bottomIndex),
      bottomNavigationBar: BottomNavigationBar(
        currentIndex: bottomIndex,
        onTap: (i) => ref.read(bottomTabIndexProvider.notifier).state = i,
        items: const [
          BottomNavigationBarItem(icon: Icon(Icons.home), label: 'Home'),
          BottomNavigationBarItem(icon: Icon(Icons.list_alt), label: 'Order'),
          BottomNavigationBarItem(icon: Icon(Icons.qr_code_scanner), label: 'Scan QR'),
          BottomNavigationBarItem(icon: Icon(Icons.timer), label: 'Waiting'),
          BottomNavigationBarItem(icon: Icon(Icons.mail), label: 'Inbox'),
        ],
      ),
    );
  }

  Widget _buildBody(int index) {
    switch (index) {
      case 0:
        return const BrandHomePage();   // 👈 tergantung brand + API
      case 1:
        return const OrderPage();       // global (tidak tergantung brand)
      case 2:
        return const ScanQrPage();
      case 3:
        return const WaitingPage();
      case 4:
        return const InboxPage();
      default:
        return const SizedBox.shrink();
    }
  }
}

/// ==== HOME PAGE (HIT API BERDASARKAN BRAND) ====

class BrandHomePage extends ConsumerWidget {
  const BrandHomePage({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final brand = ref.watch(selectedBrandProvider);
    final asyncData = ref.watch(homeDataProvider(brand)); // initial hit API per brand

    return asyncData.when(
      data: (data) {
        // Pilih widget dashboard per brand
        switch (brand) {
          case Brand.a:
            return BrandADashboard(data: data);
          case Brand.b:
            return BrandBDashboard(data: data);
          case Brand.c:
            return BrandCDashboard(data: data);
          case Brand.d:
            return BrandDDashboard(data: data);
          case Brand.e:
            return BrandEDashboard(data: data);
        }
      },
      loading: () => const Center(child: CircularProgressIndicator()),
      error: (e, _) => Center(child: Text('Error: $e')),
    );
  }
}

