  final String id;
  final String name;
  final double price;

  const MenuItem({required this.id, required this.name, required this.price});
}
```

### 2) Repository — `menu_repository.dart`
```dart
// lib/brands/sushitei/features/home/data/menu_repository.dart
import '../models/menu_item.dart';

abstract class MenuRepository {
  Future<List<MenuItem>> fetchMenu();
}

class MenuRepositoryImpl implements MenuRepository {
  // inject http client or dio in constructor if needed
  @override
  Future<List<MenuItem>> fetchMenu() async {
    // stub: replace with real http call
    await Future.delayed(const Duration(milliseconds: 500));
    return [
      MenuItem(id: '1', name: 'Salmon Roll', price: 5.5),
      MenuItem(id: '2', name: 'Miso Soup', price: 2.0),
    ];
  }
}
```

### 3) Provider / Notifier — `menu_provider.dart`
```dart
// lib/brands/sushitei/features/home/providers/menu_provider.dart
import 'package:flutter_riverpod/flutter_riverpod.dart';
import '../models/menu_item.dart';
import '../data/menu_repository.dart';

final menuRepositoryProvider = Provider<MenuRepository>((ref) {
  return MenuRepositoryImpl(); // or injected from app-level override
});

// Use AsyncNotifier (Riverpod 2) or StateNotifier with AsyncValue.
// Example with StateNotifier + AsyncValue:

class MenuState {
  final AsyncValue<List<MenuItem>> items;
  const MenuState({this.items = const AsyncValue.loading()});
  MenuState copyWith({AsyncValue<List<MenuItem>>? items}) {
    return MenuState(items: items ?? this.items);
  }
}

class MenuNotifier extends StateNotifier<MenuState> {
  MenuNotifier(this._repo) : super(const MenuState()) {
    loadMenu();
  }

  final MenuRepository _repo;

  Future<void> loadMenu() async {
    state = state.copyWith(items: const AsyncValue.loading());
    try {
      final list = await _repo.fetchMenu();
      state = state.copyWith(items: AsyncValue.data(list));
    } catch (e, st) {
      state = state.copyWith(items: AsyncValue.error(e, st));
    }
  }

  // Example action
  Future<void> refresh() => loadMenu();
}

final menuNotifierProvider =
    StateNotifierProvider<MenuNotifier, MenuState>((ref) {
  final repo = ref.watch(menuRepositoryProvider);
  return MenuNotifier(repo);
});
```

### 4) UI — `home_screen.dart`
```dart
// lib/brands/sushitei/features/home/ui/home_screen.dart
import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import '../providers/menu_provider.dart';

class HomeScreen extends ConsumerWidget {
  const HomeScreen({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final menuState = ref.watch(menuNotifierProvider);

    return Scaffold(
      appBar: AppBar(title: const Text('Menu')),
      body: menuState.items.when(
        data: (items) {
          if (items.isEmpty) return const Center(child: Text('No items'));
          return RefreshIndicator(
            onRefresh: () => ref.read(menuNotifierProvider.notifier).refresh(),
            child: ListView.builder(
              itemCount: items.length,
              itemBuilder: (context, i) {
                final item = items[i];
                return ListTile(
                  title: Text(item.name),
                  subtitle: Text('\$${item.price.toStringAsFixed(2)}'),
                );
              },
            ),
          );
        },
        loading: () => const Center(child: CircularProgressIndicator()),
        error: (err, st) => Center(
          child: Column(
            mainAxisSize: MainAxisSize.min,
            children: [
              Text('Failed to load: $err'),
              const SizedBox(height: 8),
              ElevatedButton(
                onPressed: () => ref.read(menuNotifierProvider.notifier).loadMenu(),
                child: const Text('Retry'),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
```

