// routers/app_router.dart
import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:go_router/go_router.dart';

import '../core/services/network.s.dart';
import '../core/services/brand.s.dart';
import '../core/auth/auth.c.dart';
import '/routers/auth_router/auth.r.dart';
import '/routers/brand_router/brand.r.dart';
import '../pages/network_loose_page.dart'; // add this file below

/// Small ChangeNotifier that listens to providers and notifies GoRouter.
class RouterNotifier extends ChangeNotifier {
  RouterNotifier(this._ref) {
    // listen auth changes
    _ref.listen(authCoreProvider, (_, __) => notifyListeners());
    // listen brand selection changes
    _ref.listen<BrandModel?>(brandProvider, (_, __) => notifyListeners());
    // listen network status (AsyncValue<bool>)
    _ref.listen<AsyncValue<bool>>(networkStatusStreamProvider, (_, __) => notifyListeners());
  }

  final WidgetRef _ref;
}

final lastRouteBeforeOfflineProvider = StateProvider<String?>((_) => null);

class AppRouter {
  AppRouter._();

  static GoRouter createRouter(WidgetRef ref) {
    final routerNotifier = RouterNotifier(ref);

    return GoRouter(
      initialLocation: '/',
      debugLogDiagnostics: true,
      refreshListenable: routerNotifier,
      redirect: (context, state) {
        // read current states inside redirect (safe when using refreshListenable)
        final authState = ref.read(authCoreProvider);
        final bool loggedIn = authState.isLoggedIn;

        final networkAsync = ref.read(networkStatusStreamProvider);
        final bool online = networkAsync.maybeWhen(
          data: (val) => val,
          orElse: () => true, // assume online until stream says otherwise
        );

        final loc = state.location;
        final onLogin = loc == '/login';
        final onNetworkPage = loc == '/network-loose';
        final onRoot = loc == '/';

        // 1) If offline and not already on network page -> save last route & go to network page
        if (!online && !onNetworkPage) {
          // Save the current location so we can return when online again
          ref.read(lastRouteBeforeOfflineProvider.notifier).state = loc;
          return '/network-loose';
        }

        // 2) If online and currently on network page -> return to last route or default
        if (online && onNetworkPage) {
          final last = ref.read(lastRouteBeforeOfflineProvider);
          // Clear it after using
          ref.read(lastRouteBeforeOfflineProvider.notifier).state = null;
          return (last != null && last != '/network-loose') ? last : '/main';
        }

        // 3) Normal auth/brand redirects
        if (!loggedIn && !onLogin) {
          return '/login';
        }

        if (loggedIn && (onLogin || onRoot)) {
          final brand = ref.read(brandProvider);
          if (brand != null) {
            return '/main';
          }
          return '/select-brand';
        }

        // No redirect
        return null;
      },
      routes: [
        ...authRoutes,
        ...brandRoutes,
        GoRoute(
          path: '/network-loose',
          name: 'network-loose',
          builder: (context, state) => const NetworkLoosePage(),
        ),
      ],
      errorBuilder: (context, state) => Scaffold(
        appBar: AppBar(title: const Text('404')),
        body: Center(child: Text('Halaman tidak ditemukan: ${state.error}')),
      ),
    );
  }
}
```

Notes:
- `refreshListenable: routerNotifier` will cause `GoRouter` to re-run `redirect` when any watched provider changes.
- `redirect` uses `ref.read(...)` to get the latest states — this avoids `redirect` itself creating watches / subscriptions.

