```dart
redirect: (context, state) {
  final auth = ref.read(authCoreProvider);
  final brand = ref.read(brandProvider);

  // debug lebih informatif
  print('[router] location=${state.location} matched=${state.matchedLocation} auth=${auth.isLoggedIn} brand=${brand?.name}');

  final networkAsync = ref.read(networkStatusStreamProvider);
  // jangan anggap offline saat status belum tersedia
  final bool online = networkAsync.maybeWhen(
    data: (val) => val,
    loading: () => true,      // assume online while checking
    orElse: () => true,
  );
  print('[router] network online=$online');

  final bool loggedIn = auth.isLoggedIn;
  final bool onLoginPage = state.matchedLocation == '/login';
  final bool onNetworkPage = state.matchedLocation == '/network-loose';
  final bool onRoot = state.matchedLocation == '/';

  // 1) offline -> to network page (but only if not already there)
  if (!online && !onNetworkPage) {
    // simpan last route jika mau
    ref.read(lastRouteBeforeOfflineProvider.notifier).state = state.location;
    return '/network-loose';
  }

  // 2) back online and currently on network page -> return to last or main
  if (online && onNetworkPage) {
    final last = ref.read(lastRouteBeforeOfflineProvider);
    ref.read(lastRouteBeforeOfflineProvider.notifier).state = null;
    return (last != null && last != '/network-loose') ? last : '/main';
  }

  // 3) auth redirects (hindari meng-redirect ke lokasi yang sama)
  if (!loggedIn && !onLoginPage) {
    if (state.location != '/login') return '/login';
  }

  if (loggedIn && (onLoginPage || onRoot)) {
    if (brand != null) {
      if (state.location != '/main') return '/main';
    } else {
      if (state.location != '/select-brand') return '/select-brand';
    }
  }

  return null;
}
