## 2) `routers/app_router.dart` (RouterNotifier + AppRouter)
(pakai RouterNotifier yang debounce & cek perubahan agar tidak notify berkali-kali)
```dart
// routers/app_router.dart
import 'dart:async';
import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:go_router/go_router.dart';

import '../core/services/network.s.dart';
import '../core/services/brand.s.dart';
import '../core/auth/auth.c.dart';
import '/routers/auth_router/auth.r.dart';
import '/routers/brand_router/brand.r.dart';
import '../pages/network_loose_page.dart';

class RouterNotifier extends ChangeNotifier {
  RouterNotifier(this._ref, {this.debounceMs = 250}) {
    _authSub = _ref.listen(authCoreProvider, _onAnyChanged, fireImmediately: true);
    _brandSub = _ref.listen<BrandModel?>(brandProvider, _onAnyChanged, fireImmediately: true);
    _networkSub = _ref.listen<AsyncValue<bool>>(networkStatusStreamProvider, _onAnyChanged, fireImmediately: true);
  }

  final WidgetRef _ref;
  final int debounceMs;

  // provider subscriptions
  late ProviderSubscription _authSub;
  late ProviderSubscription _brandSub;
  late ProviderSubscription _networkSub;

  Timer? _debounceTimer;
  // snapshots
  bool? _lastLoggedIn;
  String? _lastBrandId;
  bool? _lastOnline;

  void _onAnyChanged(_, __) {
    // schedule a single notify for burst changes
    _debounceTimer?.cancel();
    _debounceTimer = Timer(Duration(milliseconds: debounceMs), () => _notifyIfChanged());
  }

  void _notifyIfChanged() {
    final auth = _ref.read(authCoreProvider);
    final bool loggedIn = auth.isLoggedIn;

    final brand = _ref.read(brandProvider);
    final networkAsync = _ref.read(networkStatusStreamProvider);
    final bool? online = networkAsync.maybeWhen(data: (v) => v, orElse: () => null);

    final changed = (_lastLoggedIn != loggedIn) ||
        (_lastBrandId != brand?.id) ||
        (_lastOnline != online);

    _lastLoggedIn = loggedIn;
    _lastBrandId = brand?.id;
    _lastOnline = online;

    if (changed) notifyListeners();
  }

  @override
  void dispose() {
    _debounceTimer?.cancel();
    _authSub.close();
    _brandSub.close();
    _networkSub.close();
    super.dispose();
  }
}

final lastRouteBeforeOfflineProvider = StateProvider<String?>((_) => null);

class AppRouter {
  AppRouter._();

  static GoRouter createRouter(WidgetRef ref) {
    final notifier = RouterNotifier(ref);

    return GoRouter(
      initialLocation: '/',
      debugLogDiagnostics: true,
      refreshListenable: notifier,
      redirect: (context, state) {
        final auth = ref.read(authCoreProvider);
        final brand = ref.read(brandProvider);

        final network = ref.read(networkStatusStreamProvider);
        final bool online = network.maybeWhen(data: (v) => v, loading: () => true, orElse: () => true);

        final loc = state.location;
        final onNetwork = loc == '/network-loose';
        final onLogin = loc == '/login';
        final onRoot = loc == '/';

        // offline -> go to network page (only if not already)
        if (!online && !onNetwork) {
          ref.read(lastRouteBeforeOfflineProvider.notifier).state = loc;
          if (loc != '/network-loose') return '/network-loose';
          return null;
        }

        // online and currently on network page -> return to last or /main
        if (online && onNetwork) {
          final last = ref.read(lastRouteBeforeOfflineProvider);
          ref.read(lastRouteBeforeOfflineProvider.notifier).state = null;
          final goTo = (last != null && last != '/network-loose') ? last : '/main';
          if (loc != goTo) return goTo;
          return null;
        }

        // auth redirects (example)
        final loggedIn = auth.isLoggedIn;
        if (!loggedIn && !onLogin && loc != '/login') return '/login';
        if (loggedIn && (onLogin || onRoot)) {
          if (brand != null && loc != '/main') return '/main';
          if (brand == null && loc != '/select-brand') return '/select-brand';
        }

        return null;
      },
      routes: [
        // your app routes
        ...authRoutes,
        ...brandRoutes,
        GoRoute(path: '/network-loose', builder: (c, s) => const NetworkLoosePage()),
      ],
      errorBuilder: (context, state) => Scaffold(
        appBar: AppBar(title: const Text('404')),
        body: Center(child: Text('Halaman tidak ditemukan: ${state.error}')),
      ),
    );
  }
}
```

