class BrandsAccordion extends ConsumerWidget {
  const BrandsAccordion({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    return FutureBuilder<List<BrandModel>>(
      future: BrandService().loadBrands(),
      builder: (context, snapshot) {
        if (snapshot.connectionState == ConnectionState.waiting) {
          return const Center(
            child: SizedBox(
              width: 20,
              height: 20,
              child: CircularProgressIndicator(
                strokeWidth: 2,
                valueColor: AlwaysStoppedAnimation<Color>(Colors.white),
              ),
            ),
          );
        }

        if (snapshot.hasError) {
          return const Padding(
            padding: EdgeInsets.all(16),
            child: Text("Failed to load brands", style: TextStyle(color: Colors.white70)),
          );
        }

        final brands = snapshot.data ?? [];
        final selectedBrand = ref.watch(brandProvider);

        return Theme(
          data: Theme.of(context).copyWith(
            dividerColor: Colors.transparent,
            iconTheme: const IconThemeData(color: Colors.white),
          ),
          child: ExpansionTile(
            initiallyExpanded: true,
            leading: const Icon(Icons.storefront_outlined, color: Colors.white),
            title: Text(
              selectedBrand!.name,
              style: TextStyle(color: Colors.white, fontWeight: FontWeight.w600),
            ),
            iconColor: Colors.white,
            collapsedIconColor: Colors.white,
            children: brands.map((brand) {
              final isActive = selectedBrand.id == brand.id;

              return _DrawerMenuItem(
                icon: Icons.storefront_outlined,
                label: brand.name,
                showEndIcon: true,
                endIcon: isActive ? Icons.check_circle : null,
                endIconColor: isActive ? Colors.greenAccent : Colors.white54,
                onTap: () {
                  ref.read(brandProvider.notifier).state = brand;
                  debugPrint("Selected brand: ${brand.name}");
                  Navigator.pop(context);
                },
              );
            }).toList(),
          ),
        );
      },
    );
  }
}

class _DrawerMenuItem extends StatelessWidget {
  final IconData? icon;
  final String label;
  final VoidCallback onTap;
  final bool showEndIcon;
  final IconData? endIcon;
  final Color? endIconColor;

  const _DrawerMenuItem({
    Key? key,
    this.icon,
    required this.label,
    required this.onTap,
    this.showEndIcon = false,
    this.endIcon,
    this.endIconColor,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 4),
      child: InkWell(
        borderRadius: BorderRadius.circular(14),
        onTap: onTap,
        child: Container(
          padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 10),
          decoration: BoxDecoration(
            borderRadius: BorderRadius.circular(14),
            color: Colors.white.withOpacity(0.06),
          ),
          child: Row(
            children: [
              if (icon != null) Icon(icon, color: Colors.white),
              if (icon != null) const SizedBox(width: 12),
              Expanded(
                child: Text(label, style: const TextStyle(color: Colors.white, fontSize: 14)),
              ),
              if (showEndIcon)
                Icon(
                  endIcon ?? Icons.chevron_right,
                  color: endIconColor ?? Colors.white54,
                  size: 20,
                ),
            ],
          ),
        ),
      ),
    );
  }
}


