```dart
class _BestSellerCard extends StatelessWidget {
  final String name;
  final int price;
  final int originalPrice;
  final String imageUrl;
  final Color accent;

  const _BestSellerCard({
    super.key,
    required this.name,
    required this.price,
    required this.originalPrice,
    required this.imageUrl,
    required this.accent,
  });

  String _formatCurrency(int value) {
    // simple format Rp 48.000
    return "Rp ${value.toString().replaceAllMapped(RegExp(r'(\d{3})(?=\d)'), (m) => '${m[1]}.')}";
  }

  @override
  Widget build(BuildContext context) {
    final hasDiscount = originalPrice > 0 && originalPrice > price;

    return InkWell(
      borderRadius: BorderRadius.circular(10),
      onTap: () {
        debugPrint("Best seller tapped: $name");
      },
      child: Container(
        width: 150,
        decoration: BoxDecoration(
          color: Colors.grey.shade50,
          borderRadius: BorderRadius.circular(10),
          boxShadow: [
            BoxShadow(
              blurRadius: 4,
              offset: const Offset(0, 2),
              color: Colors.black.withOpacity(0.05),
            ),
          ],
        ),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            // IMAGE
            ClipRRect(
              borderRadius: const BorderRadius.vertical(top: Radius.circular(10)),
              child: SizedBox(
                height: 110,
                width: double.infinity,
                child: Image.network(
                  imageUrl,
                  fit: BoxFit.cover,
                  errorBuilder: (_, __, ___) => Container(
                    color: Colors.grey.shade300,
                    child: const Center(child: Icon(Icons.restaurant, size: 32)),
                  ),
                ),
              ),
            ),

            // INFO
            Padding(
              padding: const EdgeInsets.symmetric(horizontal: 8, vertical: 6),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  // Nama
                  Text(
                    name,
                    maxLines: 2,
                    overflow: TextOverflow.ellipsis,
                    style: const TextStyle(
                      fontSize: 12,
                      fontWeight: FontWeight.w600,
                    ),
                  ),
                  const SizedBox(height: 4),

                  // Harga & harga coret
                  Row(
                    children: [
                      Text(
                        _formatCurrency(price),
                        style: TextStyle(
                          fontSize: 12,
                          fontWeight: FontWeight.bold,
                          color: accent,
                        ),
                      ),
                      const SizedBox(width: 6),
                      if (hasDiscount)
                        Text(
                          _formatCurrency(originalPrice),
                          style: const TextStyle(
                            fontSize: 10,
                            color: Colors.grey,
                            decoration: TextDecoration.lineThrough,
                          ),
                        ),
                    ],
                  ),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}
```

