### 📌 brand_routes.dart

```dart
import 'package:go_router/go_router.dart';
import 'package:stg/routers/brand_router/sushitei.r.dart';

final brandRoutes = <RouteBase>[
  GoRoute(
    path: '/sushitei',
    name: 'sushitei.root',
    redirect: (context, state) {
      // Kalau user buka /sushitei langsung → otomatis ke /sushitei/dashboard
      if (state.matchedLocation == '/sushitei') {
        return '/sushitei/dashboard';
      }
      return null;
    },
    routes: [
      ...sushiteiRoutes,
    ],
  ),
];
