## 🔥 Cleaner Version — No setState Needed

```dart
import 'package:flutter/material.dart';
import 'package:flutter_dotenv/flutter_dotenv.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'app.dart';
import 'core/services/brand.s.dart';

Future<void> main() async {
  WidgetsFlutterBinding.ensureInitialized();
  await dotenv.load(fileName: "env/.env.dev");
  runApp(const ProviderScope(child: FirstLoad()));
}

class FirstLoad extends ConsumerWidget {
  const FirstLoad({super.key});

  Future<void> initializeBrand(WidgetRef ref) async {
    final brands = await BrandService().loadBrands();
    ref.read(brandProvider.notifier).state =
        brands.firstWhere((e) => e.id == 1);
  }

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final brand = ref.watch(brandProvider);

    // If brand not loaded yet → show loading
    if (brand == null) {
