import 'dart:convert';
import 'package:flutter/services.dart';
import 'brand_model.dart'; // Adjust path if needed

class _FirstLoadState extends State<FirstLoad> {
  bool isLoading = true;
  List<Brand> brands = [];

  @override
  void initState() {
    super.initState();
    firstLoad();
  }

  Future<void> firstLoad() async {
    final String response = await rootBundle.loadString('assets/data/brands.json');
    final data = jsonDecode(response);

    brands = (data["brands"] as List)
        .map((b) => Brand.fromJson(b))
        .toList();

    await Future.delayed(const Duration(seconds: 2)); // splash effect

    setState(() {
      isLoading = false;
    });
  }

  @override
  Widget build(BuildContext context) {
    if (isLoading) {
      return const MaterialApp(
        home: Scaffold(body: Center(child: CircularProgressIndicator())),
      );
    }

    return App(brands: brands); // Pass loaded brands to app
  }
}
```

