/// Auth state (must extend ChangeNotifier)
final authCoreProvider = ChangeNotifierProvider<AuthCoreNotifier>((ref) {
  return AuthCoreNotifier();
});

/// Single GoRouter instance for the whole app
final goRouterProvider = Provider<GoRouter>((ref) {
  // read, not watch → we want a stable router instance
  final authCoreNotifier = ref.read(authCoreProvider);
  return AppRouter.createRouter(authCoreNotifier);
});
