```dart
class AppRouter {
  AppRouter._();

  static GoRouter createRouter(AuthCoreNotifier authCoreNotifier) {
    return GoRouter(
      initialLocation: '/', // 👈 Start dari root
      debugLogDiagnostics: true,
      refreshListenable: authCoreNotifier,

      redirect: (context, state) {
        final bool loggedIn = authCoreNotifier.isLoggedIn;
        final String location = state.matchedLocation;

        // Halaman login
        const loginRoute = '/login';
        const homeRoute = '/brands'; // setelah login

        // Jika belum login dan bukan di login → lempar ke /login
        if (!loggedIn && location != loginRoute) {
          return loginRoute;
        }

        // Jika sudah login tapi masih di /login → lempar ke /brands
        if (loggedIn && location == loginRoute) {
          return homeRoute;
        }

        // Tetap di halaman yang dituju
        return null;
      },

      routes: [
        ...authRoutes,
        ...brandRoutes,
      ],

      errorBuilder: (context, state) {
        return Scaffold(
          appBar: AppBar(title: const Text('404')),
          body: Center(child: Text('Page not found: ${state.error}')),
        );
      },
    );
  }
}
```

