### 3.3. Riverpod providers

```dart
// lib/providers.dart
import 'dart:convert';

import 'package:flutter/services.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';

import 'models/brand.dart';
import 'models/dashboard_config.dart';

final brandsProvider = FutureProvider<List<Brand>>((ref) async {
  final jsonStr = await rootBundle.loadString('assets/brands.json');
  final List<dynamic> data = json.decode(jsonStr);
  return data.map((e) => Brand.fromJson(e as Map<String, dynamic>)).toList();
});

final dashboardsProvider = FutureProvider<List<DashboardConfig>>((ref) async {
  final jsonStr = await rootBundle.loadString('assets/dashboards.json');
  final List<dynamic> data = json.decode(jsonStr);
  return data
      .map((e) => DashboardConfig.fromJson(e as Map<String, dynamic>))
      .toList();
});

/// Which brand is selected right now
final selectedBrandIdProvider = StateProvider<String?>((ref) => null);

/// Currently selected Brand
final selectedBrandProvider = Provider<Brand?>((ref) {
  final brandsAsync = ref.watch(brandsProvider);
  final selectedId = ref.watch(selectedBrandIdProvider);

  return brandsAsync.when(
    data: (brands) {
      if (selectedId == null && brands.isNotEmpty) {
        // default to first brand
        return brands.first;
      }
      return brands.firstWhere(
        (b) => b.id == selectedId,
        orElse: () => brands.isNotEmpty ? brands.first : throw Exception(),
      );
    },
    loading: () => null,
    error: (_, __) => null,
  );
});

/// Dashboard config for selected brand
final selectedDashboardConfigProvider = Provider<DashboardConfig?>((ref) {
  final dashboardsAsync = ref.watch(dashboardsProvider);
  final brand = ref.watch(selectedBrandProvider);

  if (brand == null) return null;

  return dashboardsAsync.when(
    data: (configs) {
      return configs.firstWhere(
        (c) => c.brandId == brand.id,
        orElse: () => DashboardConfig(brandId: brand.id, widgets: []),
      );
    },
    loading: () => null,
    error: (_, __) => null,
  );
});
```

