### 3.4. `main.dart` – basic UI with different dashboards

```dart
// lib/main.dart
import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';

import 'models/brand.dart';
import 'providers.dart';

void main() {
  runApp(const ProviderScope(child: MyApp()));
}

class MyApp extends ConsumerWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final selectedBrand = ref.watch(selectedBrandProvider);

    // fallback colors if not loaded yet
    final themePrimary = selectedBrand != null
        ? _hexToColor(selectedBrand.colors.primary)
        : Colors.blue;

    final themeBackground = selectedBrand != null
        ? _hexToColor(selectedBrand.colors.background)
        : Colors.white;

    return MaterialApp(
      title: 'Multi Brand Dashboard',
      theme: ThemeData(
        useMaterial3: true,
        colorScheme: ColorScheme.fromSeed(seedColor: themePrimary),
        scaffoldBackgroundColor: themeBackground,
      ),
      home: const HomeScreen(),
    );
  }
}

class HomeScreen extends ConsumerWidget {
  const HomeScreen({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final brandsAsync = ref.watch(brandsProvider);
    final selectedBrand = ref.watch(selectedBrandProvider);
    final dashboardConfig = ref.watch(selectedDashboardConfigProvider);

    return Scaffold(
      appBar: AppBar(
        title: Text(selectedBrand?.name ?? 'Loading...'),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          children: [
            // Brand selector
            brandsAsync.when(
              data: (brands) {
                return DropdownButton<String>(
                  isExpanded: true,
                  value: selectedBrand?.id ?? (brands.isNotEmpty ? brands.first.id : null),
                  items: brands
                      .map(
                        (b) => DropdownMenuItem(
                          value: b.id,
                          child: Text(b.name),
                        ),
                      )
                      .toList(),
                  onChanged: (value) {
                    if (value == null) return;
                    ref.read(selectedBrandIdProvider.notifier).state = value;
                  },
                );
              },
              loading: () => const LinearProgressIndicator(),
              error: (e, _) => Text('Error loading brands: $e'),
            ),
            const SizedBox(height: 16),

            // Dashboard sections
            Expanded(
              child: dashboardConfig == null
                  ? const Center(child: CircularProgressIndicator())
                  : ListView.builder(
                      itemCount: dashboardConfig.widgets.length,
                      itemBuilder: (context, index) {
                        final widgetKey = dashboardConfig.widgets[index];
                        return Padding(
                          padding: const EdgeInsets.only(bottom: 12.0),
                          child: _buildDashboardSection(
                            context,
                            widgetKey,
                            selectedBrand,
                          ),
                        );
                      },
                    ),
            ),
          ],
        ),
      ),
    );
  }
}

/// Builds a placeholder UI for each widget key
Widget _buildDashboardSection(
  BuildContext context,
  String key,
  Brand? brand,
) {
  final secondaryColor = brand != null
      ? _hexToColor(brand.colors.secondary)
      : Colors.grey;

  Widget child;
  String title;

  switch (key) {
    case 'banner':
      title = 'Banner';
      child = const Text('Big promotional banner here');
      break;
    case 'nearby_outlet':
      title = 'Nearby Outlet';
      child = const Text('List of nearby outlets');
      break;
    case 'hot_deals':
      title = 'Hot Deals';
      child = const Text('Hot deals list/grid');
      break;
    case 'promo':
      title = 'Promo';
      child = const Text('Current promotions');
      break;
    case 'categories':
      title = 'Categories';
      child = const Text('Product categories');
      break;
    case 'recommended':
      title = 'Recommended For You';
      child = const Text('Recommended products');
      break;
    case 'voucher':
      title = 'Vouchers';
      child = const Text('Available vouchers');
      break;
    case 'hero_image':
      title = 'Hero Image';
      child = const Text('Large hero image / slider');
      break;
    case 'trending':
      title = 'Trending';
      child = const Text('Trending products or posts');
      break;
    case 'stories':
      title = 'Stories';
      child = const Text('Instagram-like stories');
      break;
    case 'featured_products':
      title = 'Featured Products';
      child = const Text('Featured products carousel');
      break;
    case 'flash_sale':
      title = 'Flash Sale';
      child = const Text('Flash sale countdown + items');
      break;
    default:
      title = key;
      child = Text('Unknown section: $key');
  }

  return Card(
    elevation: 2,
    shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
    child: Padding(
      padding: const EdgeInsets.all(16.0),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(
            title,
            style: Theme.of(context).textTheme.titleMedium?.copyWith(
                  color: secondaryColor,
                  fontWeight: FontWeight.bold,
                ),
          ),
          const SizedBox(height: 8),
          child,
        ],
      ),
    ),
  );
}

Color _hexToColor(String hex) {
  final buffer = StringBuffer();
  if (hex.length == 6 || hex.length == 7) buffer.write('ff');
  buffer.write(hex.replaceFirst('#', ''));
  return Color(int.parse(buffer.toString(), radix: 16));
}
