import 'package:flutter/material.dart';
import '../models/brand.dart';
import 'brand_theme.dart';

class BrandRegistry {
  static const brandA = Brand(id: 'brand_a', name: 'Brand A', logoAsset: 'assets/images/brand_a/logo.png');
  static const brandB = Brand(id: 'brand_b', name: 'Brand B', logoAsset: 'assets/images/brand_b/logo.png');
  static const brandC = Brand(id: 'brand_c', name: 'Brand C', logoAsset: 'assets/images/brand_c/logo.png');
  static const brandD = Brand(id: 'brand_d', name: 'Brand D', logoAsset: 'assets/images/brand_d/logo.png');
  static const brandE = Brand(id: 'brand_e', name: 'Brand E', logoAsset: 'assets/images/brand_e/logo.png');

  static const all = [brandA, brandB, brandC, brandD, brandE];

  static Brand byId(String id) =>
      all.firstWhere((b) => b.id == id, orElse: () => brandA);
}

class BrandThemes {
