    'brand_b': BrandTheme(
      primary: Colors.green,
      background: Colors.white,
      accent: Colors.greenAccent,
    ),
    // ... brand_c, d, e
  };

  static BrandTheme byId(String id) =>
      map[id] ?? map['brand_a']!;
}
